      "import cellchat\n",
      "\n",
      "# Load input data\n",
      "data = pd.read_csv('data_input.csv', index_col=0)\n",
      "meta = pd.read_csv('meta.csv', index_col=0)\n",
      "\n",
      "# Initialize CellChat object\n",
      "cellchat_obj = cellchat.CellChat(data, meta)\n",
